 Log.d(
 "internet?:::",
 "isConnected: isListening=$isListening isAvailable=$isAvailable isBlocked=$isBlocked networkCapabilities=$capabilitiesValid"
 )

 return isListening &&
 isAvailable &&
 !isBlocked &&
 capabilitiesValid
 }

